import React, { Component } from 'react';

import WelcomeMenu from './component/menu/home-menu';
import SideMenu from './component/menu/side-menu';
import Header from './component/header';
import DisplayClients from "./component/clients/index";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars, faTimesCircle } from '@fortawesome/free-solid-svg-icons'

import {
  BrowserRouter,
  Switch,
  Route,
  Link
} from "react-router-dom";
import './App.scss';



class App extends Component {
    constructor(){
      super();
      this.state={
        showSideMenu: false,
        mobileStyle: {
          sidePanelPlacement: { 
            left: this.state.showSideMenu ? '0rem' : '-19rem'
           },
          menuBar: {
            display: this.showSideMenu ? 'none' : 'block',
            left: this.showSideMenu ? '' : '20rem',
            cursor: 'pointer',
            color: '#4C8C72'
          },
          menuClose: {
            display: this.showSideMenu ? 'block' : 'none',
            cursor: 'pointer',
            color: '#4C8C72'
          }
        }
      }
    }

    toggleSideBar = () => {
      this.setState({
        mobileStyle: {
          menuBar: {
            display: this.showSideMenu ? 'none' : 'block',
          },
          menuClose: {
            display: this.showSideMenu ? 'block' : 'none',
          }
        }        
      })
    }


  render() {
    return (
      <BrowserRouter>
        <div className="app">
          <span className='HeaderPlacement'>
            <Header />
          </span>
          <span className='sidePanelPlacement'
          style={ this.state.mobileStyle.sidePanelPlacement }>
            <FontAwesomeIcon onClick={this.toggleSideBar} className='icon' icon={faBars}
             style={ this.state.mobileStyle.menuBar }/>
            <FontAwesomeIcon onClick={this.toggleSideBar}  className='icon' icon={faTimesCircle} 
            style={ this.state.mobileStyle.menuClose} />

            <SideMenu />
          </span>
          <Switch>
            <span className='middle-placement'>
              <Route exact path='/' component={WelcomeMenu} />
              <Route exact path='/clients' component={DisplayClients} />
            </span>
          </Switch>
        </div>
      </BrowserRouter>
    );
  }
}


// CLIENT

//ADMAIN
/*
see users and last purchase
email users
see lotto

**/




export default App;
